import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';
import { DeckGL, ScatterplotLayer } from 'deck.gl';

import geoJson from './sydney-parks.json';
import vehicleData from './vehicleData.json';

import './Map.css';

mapboxgl.accessToken = 'pk.eyJ1Ijoic3RlZmFuZHMiLCJhIjoiY2xpbDhieGVsMDJ6cDNkcnRlZHd1Mm9oMCJ9.e2hVvAkDhSwaGmT9jCl83w';

export default function App() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const deckgl = useRef(null);
  const [lng, setLng] = useState(151.20);
  const [lat, setLat] = useState(-33.86);
  const [zoom, setZoom] = useState(12);
  const [popup, setPopup] = useState(null);

  const resizeMarkers = () => {
    const newZoom = map.current.getZoom();
    setZoom(newZoom);
    mapContainer.current.style.fontSize = `${newZoom}px`;
  };

  useEffect(() => {
    if (map.current) return;
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [lng, lat],
      zoom: zoom
    });

    // Initialize deck.gl
    deckgl.current = new DeckGL({
      map: map.current, // using Mapbox as the base map
      controller: true,
      onViewStateChange: ({viewState}) => {
        setLng(viewState.longitude);
        setLat(viewState.latitude);
        setZoom(viewState.zoom);
      }
    });

    map.current.on('zoom', resizeMarkers);

    map.current.on('load', () => {
      map.current.loadImage(
        "https://docs.mapbox.com/mapbox-gl-js/assets/custom_marker.png",
        function (error, image) {
          if (error) throw error;
          map.current.addImage("custom-marker", image);

          map.current.addSource("location-points", {
            type: "geojson",
            data: {
              type: "FeatureCollection",
              features: geoJson.features,
            },
          });

          map.current.addLayer({
            id: "location-points",
            type: "symbol",
            source: "location-points",
            layout: {
              "icon-image": "custom-marker",
              "text-field": ["get", "title"],
              "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],
              "text-offset": [0, 1.25],
              "text-anchor": "top",
            },
          });

          var locationPopup = new mapboxgl.Popup({
            closeButton: false,
            closeOnClick: false
          });

          map.current.on('mouseenter', 'location-points', function (e) {
            map.current.getCanvas().style.cursor = 'pointer';

            var coordinates = e.features[0].geometry.coordinates.slice();
            var description = e.features[0].properties.description;

            while (Math.abs(e.lngLat.lng - coordinates[0]) > 180) {
              coordinates[0] += e.lngLat.lng > coordinates[0] ? 360 : -360;
            }

            locationPopup
              .setLngLat(coordinates)
              .setHTML(description)
              .addTo(map.current);
          });

          map.current.on('mouseleave', 'location-points', function () {
            map.current.getCanvas().style.cursor = '';
            locationPopup.remove();
          });

          // Click anywhere to close the currently displayed popup
          map.current.on('click', function () {
            if (popup) {
              popup.remove();
              setPopup(null);
            }
          });
        });
    });

    return () => {
      map.current.remove();
      if (deckgl.current) {
        deckgl.current.finalize(); // important to clean up deck.gl resources
      }
    };
  }, []);

  useEffect(() => {
    // Ensure deck.gl is initialized
    if (!deckgl.current) return;

    // Add a new ScatterplotLayer
    const scatterplotLayer = new ScatterplotLayer({
      id: 'scatterplot-layer',
      data: vehicleData,
      getPosition: d => [d.lon, d.lat],
      getRadius: 100, // Size of each marker
      getFillColor: [255, 140, 0], // Color of markers
    });

    // Set the layers property
    deckgl.current.setProps({
      layers: [scatterplotLayer],
    });

  }, [vehicleData]);

  return (
    <div>
      <div ref={mapContainer} className="map-container" style={{fontSize: `${zoom}px`}} />
    </div>
  );
}
