import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax

// add import for GeoJSON data
import geoJson from './chicago-parks.json';

mapboxgl.accessToken = 'pk.eyJ1Ijoic3RlZmFuZHMiLCJhIjoiY2xpbDhieGVsMDJ6cDNkcnRlZHd1Mm9oMCJ9.e2hVvAkDhSwaGmT9jCl83w';

export default function App() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(151.20);
  const [lat, setLat] = useState(-33.86);
  const [zoom, setZoom] = useState(12);

  // Assume you have the vehicle data in an array
  const vehicleData = [
    // Array of vehicle data with lat, lon, and status
  ];

  useEffect(() => {
    if (map.current) return; // initialize map only once
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('load', () => {
      // Custom marker for location
      map.current.loadImage(
        "https://docs.mapbox.com/mapbox-gl-js/assets/custom_marker.png",
        function (error, image) {
          if (error) throw error;
          map.current.addImage("custom-marker", image);

          // Add a GeoJSON source with location points
          map.current.addSource("location-points", {
            type: "geojson",
            data: {
              type: "FeatureCollection",
              features: geoJson.features,
            },
          });

          // Add a symbol layer for location
          map.current.addLayer({
            id: "location-points",
            type: "symbol",
            source: "location-points",
            layout: {
              "icon-image": "custom-marker",
              "text-field": ["get", "title"],
              "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],
              "text-offset": [0, 1.25],
              "text-anchor": "top",
            },
          });

          // Vehicle marker
          map.current.addSource('vehicles', {
            'type': 'geojson',
            'data': {
              'type': 'FeatureCollection',
              'features': vehicleData.map((vehicle) => ({
                'type': 'Feature',
                'geometry': {
                  'type': 'Point',
                  'coordinates': [vehicle.lon, vehicle.lat]
                },
                'properties': {
                  'status': vehicle.status
                }
              }))
            }
          });

          map.current.addLayer({
            'id': 'vehicles',
            'type': 'circle',
            'source': 'vehicles',
            'paint': {
              'circle-radius': 10,
              'circle-color': ['match', ['get', 'status'], 'active', '#008000', '#FF0000']
            }
          });

          // Create a popup, but don't add it          map.current.on('mouseenter', 'vehicles', function (e) {
            // Change the cursor style as a UI indicator.
            map.current.getCanvas().style.cursor = 'pointer';

            var coordinates = e.features[0].geometry.coordinates.slice();
            var status = e.features[0].properties.status;

            // Ensure that if the map is zoomed out such that multiple
            // copies of the feature are visible, the popup appears
            // over the copy being pointed to.
            while (Math.abs(e.lngLat.lng - coordinates[0]) > 180) {
              coordinates[0] += e.lngLat.lng > coordinates[0] ? 360 : -360;
            }

            // Populate the popup and set its coordinates
            // based on the feature found.
            new mapboxgl.Popup()
              .setLngLat(coordinates)
              .setHTML('Vehicle status: ' + status)
              .addTo(map.current);
          });

          map.current.on('mouseleave', 'vehicles', function () {
            map.current.getCanvas().style.cursor = '';
            // Remove the popup when the mouse leaves.
            map.current.getCanvas().style.cursor = '';
            map.current.getCanvas().style.cursor = '';
          });
        });

        // Clean up on unmount
        return () => map.current.remove();
  }, []);

    return (
    <div>
        <div ref={mapContainer} className="map-container" />
    </div>
    );
}
