import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';

import geoJson from './sydney-parks.json';
import vehicleData from './vehicleData.json';

import './Map.css';

mapboxgl.accessToken = 'pk.eyJ1Ijoic3RlZmFuZHMiLCJhIjoiY2xpbDhieGVsMDJ6cDNkcnRlZHd1Mm9oMCJ9.e2hVvAkDhSwaGmT9jCl83w';

export default function App() {
  const mapContainer = useRef(null); 
  const map = useRef(null); 

  // Calculate the average latitude and longitude
  const avgLat = vehicleData.reduce((acc, curr) => acc + curr.lat, 0) / vehicleData.length;
  const avgLon = vehicleData.reduce((acc, curr) => acc + curr.lon, 0) / vehicleData.length;

  const [lng, setLng] = useState(avgLon); 
  const [lat, setLat] = useState(avgLat);
  const [zoom, setZoom] = useState(12); 
  const [popup, setPopup] = useState(null); 

  const resizeMarkers = () => {
    const newZoom = map.current.getZoom();
    setZoom(newZoom);
    mapContainer.current.style.fontSize = `${newZoom}px`;
  };

  // Function to load marker image and set up map layers
  const loadMarkerImageAndSetupLayers = () => {
    map.current.loadImage(
      "https://docs.mapbox.com/mapbox-gl-js/assets/custom_marker.png",
      function (error, image) {
        if (error) throw error;
        map.current.addImage("custom-marker", image);

        map.current.addSource("location-points", {
          type: "geojson",
          data: {
            type: "FeatureCollection",
            features: geoJson.features,
          },
        });

        map.current.addLayer({
          id: "location-points",
          type: "symbol",
          source: "location-points",
          layout: {
            "icon-image": "custom-marker",
            "text-field": ["get", "title"],
            "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],
            "text-offset": [0, 1.25],
            "text-anchor": "top",
          },
        });
      }
    );
  }

  // Function to show a popup when a location point is hovered
  const setupPopupOnHover = () => {
    var locationPopup = new mapboxgl.Popup({
      closeButton: false,
      closeOnClick: false
    });

    map.current.on('mouseenter', 'location-points', function (e) {
      map.current.getCanvas().style.cursor = 'pointer';

      var coordinates = e.features[0].geometry.coordinates.slice();
      var description = e.features[0].properties.description;

      while (Math.abs(e.lngLat.lng - coordinates[0]) > 180) {
        coordinates[0] += e.lngLat.lng > coordinates[0] ? 360 : -360;
      }

      locationPopup
        .setLngLat(coordinates)
        .setHTML(description)
        .addTo(map.current);
    });

    map.current.on('mouseleave', 'location-points', function () {
      map.current.getCanvas().style.cursor = '';
      locationPopup.remove();
    });
  }

  // Function to close any displayed popup when clicking anywhere
  const closePopupOnClickAnywhere = () => {
    map.current.on('click', function () {
      if (popup) {
        popup.remove();
        setPopup(null);
      }
    });
  }

  // Function to add vehicle markers to the map
  const addVehicleMarkers = () => {
    vehicleData.forEach(vehicle => {
      var el = document.createElement('div');
      el.className = 'marker ' + vehicle.status;

      const vehiclePopup = new mapboxgl.Popup({ offset: 25 })
        .setHTML(`
          <h3>${vehicle.ZID}</h3>
          <p>Status: ${vehicle.status}</p>
          <p>Home: ${vehicle.home}</p>
        `);

      var marker = new mapboxgl.Marker(el)
        .setLngLat([vehicle.lon, vehicle.lat])
        .setPopup(vehiclePopup)
        .addTo(map.current);

      el.addEventListener('mouseenter', () => vehiclePopup.addTo(map.current));
      el.addEventListener('mouseleave', () => {
        if (popup !== vehiclePopup) {
          vehiclePopup.remove();
        }
      });
      el.addEventListener('click', () => {
        if (popup) {
          popup.remove();
        }
        setPopup(vehiclePopup);
      });
    });
  }

  useEffect(() => {
    if (map.current) return;
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('zoom', resizeMarkers);

    map.current.on('load', () => {
      loadMarkerImageAndSetupLayers();
      setupPopupOnHover();
      closePopupOnClickAnywhere();
    });

    return () => map.current.remove();
  }, []);

  useEffect(() => {
    if (!map.current) return;
    addVehicleMarkers();
  }, [vehicleData]);

  return (
    <div>
      <div ref={mapContainer} className="map-container" style={{fontSize: `${zoom}px`}} />
    </div>
  );
}
